// src/components/Projects/ProjectForm.js
import React, { useState } from 'react';
import { createProject, updateProject } from '../../services/projects';

const ProjectForm = ({ project, onSuccess }) => {
  const [name, setName] = useState(project ? project.name : '');
  const [description, setDescription] = useState(project ? project.description : '');
  const [startDate, setStartDate] = useState(project ? project.startDate : '');
  const [endDate, setEndDate] = useState(project ? project.endDate : '');
  const [location, setLocation] = useState(project ? project.location : '');
  const [status, setStatus] = useState(project ? project.status : 'Not Started');
  const [progress, setProgress] = useState(project ? project.progress : 0);
  const [funds, setFunds] = useState(project ? project.funds : 0);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const projectData = { name, description, startDate, endDate, location, status, progress, funds };

    try {
      if (project) {
        await updateProject(project._id, projectData);
      } else {
        await createProject(projectData);
      }
      onSuccess();
    } catch (error) {
      console.error('Error during project submission:', error.response?.data || error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
      </div>
      <div>
        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
      </div>
      <div>
        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
      </div>
      <div>
        <label>Location:</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
      </div>
      <div>
        <label>Status:</label>
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="Not Started">Not Started</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>
      </div>
      <div>
        <label>Progress:</label>
        <input
          type="number"
          value={progress}
          onChange={(e) => setProgress(e.target.value)}
        />
      </div>
      <div>
        <label>Funds:</label>
        <input
          type="number"
          value={funds}
          onChange={(e) => setFunds(e.target.value)}
        />
      </div>
      <button type="submit">{project ? 'Update Project' : 'Create Project'}</button>
    </form>
  );
};

export default ProjectForm;
