import api from './api';

export const registerAgency = async (credentials) => {
  const response = await api.post('/agencies/register', credentials);
  return response.data;
};

export const loginAgency = async (credentials) => {
  const response = await api.post('/auth', credentials); // Asegúrate de que la ruta sea correcta
  const { token, agencyId } = response.data; // Extrae token y agencyId de la respuesta
  localStorage.setItem('token', token); // Guarda el token en localStorage
  if (agencyId) {
    localStorage.setItem('agencyId', agencyId); // Guarda el agencyId en localStorage si está presente
  }
  return response.data;
};

export const authenticateAgency = async (credentials) => {
  const response = await api.post('/auth', credentials);
  return response.data;
};
