// src/components/Auth/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Importa useNavigate
import { loginAgency } from '../../services/auth';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Inicializa useNavigate


  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Reset error message

    try {
      const response = await loginAgency({
        email,
        password,
      });
      console.log('Response:', response);
      localStorage.setItem('authToken', response.token); // Guarda el token en localStorage
      navigate('/dashboard'); // Redirige al usuario a la página de dashboard después del registro exitoso

    } catch (error) {
      if (error.response) {
        const errors = error.response.data.errors;
        if (errors) {
          const errorMessages = errors.map(err => err.msg);
          setErrorMessage(errorMessages.join(' '));
        } else if (error.response.data.msg) {
          setErrorMessage(error.response.data.msg);
        }
      } else {
        setErrorMessage('An unexpected error occurred.');
        console.error('Error during login:', error.message);
      }
    }
  };

  return (
    <div>
      <h1>Agency Login</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {errorMessage && (
        <div className="error-message" style={{ color: 'red' }}>
          {errorMessage}
        </div>
      )}
    </div>
  );
};

export default Login;
